age': undefined,\n            'initialMargin': undefined,\n            'initialMarginPercentage': undefined,\n            'leverage': leverage,\n            'liquidationPrice': undefined,\n            'marginRatio': undefined,\n            'marginMode': undefined,\n            'percentage': undefined,\n        });\n    }"
