      "signature" => "parseTrade (trade: Dict, market: Market = undefined): Trade",
      "source" => "parseTrade (trade: Dict, market: Market = undefined): Trade {\n        //\n        // fetchTrades (public)\n        //\n        //     {\n        //         \"id\":\"ETH-BTC:3331886\",\n        //         \"price\":\"0.022981\",\n        //         \"quantity\":\"12.337\",\n        //         \"time\":\"2020-04-12T20:55:42.371Z\",\n        //         \"side\":\"sell\",\n        //         \"tick_direction\":\"down\"\n        //     }\n        //\n        // fetchMyTrades (private)\n        //\n        //     {\n        //         \"id\":\"BTC-USDT:183566\",\n        //         \"order_id\":\"17209376\",\n        //         \"side\":\"sell\",\n        //         \"fee_amount\":\"0.657396569175\",\n        //         \"fee_currency_id\":\"USDT\",\n        //         \"status\":\"settled\",\n        //         \"price\":\"6573.96569175\",\n        //         \"quantity\":\"0.1\",\n        //         \"cost\":\"657.396569175\",\n        //         \"time\":\"2018-08-10T06:06:46.000Z\",\n        //         \"market_id\":\"BTC-USDT\"\n        //     }\n        //\n        const timestamp = this.parse8601 (this.safeString (trade, 'time'));\n        const id = this.safeString (trade, 'id');\n        let marketId: Str = undefined;\n        if (id !== undefined) {\n            const parts = id.split (':');\n            marketId = this.safeString (parts, 0);\n        }\n        marketId = this.safeString (trade, 'market_id', marketId);\n        const symbol = this.safeSymbol (marketId, market, '-');\n        const side = this.safeString (trade, 'side');\n        const priceString = this.safeString (trade, 'price');\n        const amountString = this.safeString (trade, 'quantity');\n        const orderId = this.safeString (trade, 'order_id');\n        const feeCostString = this.safeString (trade, 'fee_amount');\n        let fee = undefined;\n        if (feeCostString !== undefined) {\n            const feeCurrencyId = this.safeString (trade, 'fee_currency_id');\n            const feeCurrencyCode = this.safeCurrencyCode (feeCurrencyId);\n            fee = {\n                'cost': feeCostString,\n                'currency': feeCurrencyCode,\n            };\n        }\n        return this.safeTrade ({\n            'id': id,\n            'info': trade,\n            'timestamp': timestamp,\n            'datetime': this.iso8601 (timestamp),\n            'symbol': symbol,\n            'order': orderId,\n            'type': undefined,\n            'side': side,\n            'takerOrMaker': undefined,\n            'price': priceString,\n            'amount': amountString,\n            'cost': undefined,\n            'fee': fee,\n        }, market);\n    }"
